import Header from "./Header";
import Principal from "./Principal";
import Foooter from "./Foooter";
import "bootstrap/dist/css/bootstrap.min.css";
import BotonFace from "./BotonFace";
import { useState } from "react";

function App() {
  const [contador, setContador] = useState(0);

  const [nombre, setNombre] = useState(["Benjamin", "Orellana"]);

  function incrementar() {
    setContador(contador + 1);
  }

  const decrementar = () => {
    if (contador > 0) setContador(contador - 1);

    if (contador === 0) {
      alert("hola");

      alert("chau");
    }
  };

  const reiniciar = () => {
    setContador(0);
  };
  // const incrementarF = () => {
  //   setContador(contador + 1);

  // }
  return (
    <div className="App">
      <h1>NUMERO {contador}</h1>

      <button className="btn btn-primary" onClick={incrementar}>
        INCREMENTAR
      </button>

      <button className="btn btn-danger" onClick={decrementar}>
        RESTAR
      </button>

      <button className="btn btn-warning  " onClick={reiniciar}>
        REINICIAR
      </button>

      {
                // es mayor principal si no es mayor footer
        contador >= 10 ? <Foooter />  : <Principal></Principal>
                        //if            else
        }
    </div>
  );
}

export default App;
