import Header from "./Header";
import Principal from "./Principal";
import Foooter from "./Foooter";
import "bootstrap/dist/css/bootstrap.min.css";
import BotonFace from "./BotonFace";
import { useState } from "react";

function App() {
  const [contador, setContador] = useState(1);

  const [nombre , setNombre] = useState(["Benjamin","Orellana", "Edad: 20"]);

  const incrementar2 = () =>  {
    setContador(contador + 1)
  }


  const decrementar = () =>  {
    setContador(contador - 1 )
  }


  const reiniciar = () =>  {
    setContador(1)
  }
  return (
    <div className="App">
        <button 
          className="btn btn-primary"
        onClick={incrementar2}>Clickear</button>

<button 
          className="btn btn-primary"
        onClick={decrementar}>Clickear</button>

<button 
          className="btn btn-primary"
        onClick={reiniciar}>Clickear</button>

      <Header
        nombre={nombre}
      ></Header>

    <h1>NUMERO : {contador}</h1>

    </div>
  );
}

export default App;
